cat >> website/style.css <<EOF

/* Mobile Menu Styles */
.mobile-menu-btn {
    display: none;
    background: transparent;
    border: none;
    cursor: pointer;
    padding: 8px;
    z-index: 101;
}

.hamburger-icon {
    display: block;
    width: 24px;
    height: 2px;
    background: var(--color-text);
    position: relative;
    transition: all 0.3s;
}

.hamburger-icon::before,
.hamburger-icon::after {
    content: '';
    position: absolute;
    width: 24px;
    height: 2px;
    background: var(--color-text);
    transition: all 0.3s;
}

.hamburger-icon::before {
    top: -8px;
}

.hamburger-icon::after {
    top: 8px;
}

.mobile-menu-btn.active .hamburger-icon {
    background: transparent;
}

.mobile-menu-btn.active .hamburger-icon::before {
    top: 0;
    transform: rotate(45deg);
}

.mobile-menu-btn.active .hamburger-icon::after {
    top: 0;
    transform: rotate(-45deg);
}

/* Button Styles */
.btn-white {
    background: white;
    color: var(--color-primary);
}

.btn-white:hover {
    background: #f0f0f0;
    transform: translateY(-2px);
    box-shadow: var(--shadow-md);
}

.btn-outline-white {
    border: 1px solid white;
    color: white;
    background: transparent;
}

.btn-outline-white:hover {
    background: rgba(255, 255, 255, 0.1);
    transform: translateY(-2px);
}

/* Install Section Styles */
.install-content {
    text-align: center;
}

.install-description {
    margin-bottom: 32px;
}

.install-row {
    display: flex;
    gap: 24px;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
}

.install-actions {
    display: flex;
    gap: 16px;
    margin-bottom: 0;
}

.command-box {
    margin: 0;
}

/* Responsive Adjustments */
@media (max-width: 768px) {
    .mobile-menu-btn {
        display: block;
    }

    .nav-menu {
        position: fixed;
        top: 60px;
        left: 0;
        right: 0;
        background: var(--color-bg);
        flex-direction: column;
        padding: 24px;
        border-bottom: 1px solid var(--color-border);
        transform: translateY(-150%);
        transition: transform 0.3s ease;
        box-shadow: var(--shadow-lg);
        align-items: stretch;
    }

    .nav-menu.active {
        transform: translateY(0);
    }

    .nav-item {
        padding: 12px 0;
        border-bottom: 1px solid var(--color-border);
        text-align: center;
        font-size: 16px;
    }

    .nav-cta {
        margin-top: 16px;
        text-align: center;
    }

    .theme-toggle {
        margin: 16px auto 0;
    }

    .hero-title {
        font-size: 32px;
        white-space: normal;
        line-height: 1.2;
    }

    .hero-badges {
        flex-direction: column;
        align-items: center;
        gap: 12px;
    }

    .problem-solution-layout {
        grid-template-columns: 1fr;
        gap: 32px;
    }

    .screenshot-content {
        max-height: 300px;
    }

    .install-row {
        flex-direction: column;
        gap: 24px;
    }

    .install-actions {
        flex-direction: column;
        width: 100%;
    }

    .btn {
        width: 100%;
        text-align: center;
    }

    .command-box {
        width: 100%;
        flex-direction: column;
        align-items: stretch;
        text-align: center;
    }

    .command-box code {
        margin-bottom: 8px;
        word-break: break-all;
    }
}
EOF

cat >> website/style.css <<EOF

/* Interactive Examples */
.interactive-example .code-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 16px;
}

.example-controls {
    display: flex;
    gap: 12px;
    align-items: center;
}

.view-toggle {
    display: flex;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 6px;
    padding: 2px;
}

.toggle-btn {
    background: transparent;
    border: none;
    color: #a0aec0;
    padding: 4px 12px;
    font-size: 12px;
    font-weight: 600;
    cursor: pointer;
    border-radius: 4px;
    transition: all 0.2s;
}

.toggle-btn.active {
    background: var(--color-primary);
    color: white;
}

.toggle-btn:hover:not(.active) {
    color: white;
}

.fullscreen-btn {
    background: transparent;
    border: none;
    color: #a0aec0;
    cursor: pointer;
    font-size: 16px;
    padding: 4px;
    transition: color 0.2s;
    display: flex;
    align-items: center;
    justify-content: center;
}

.fullscreen-btn:hover {
    color: white;
}

.example-content {
    position: relative;
    background: var(--code-bg);
    min-height: 200px;
}

.view-pane {
    display: none;
    width: 100%;
}

.view-pane.active {
    display: block;
}

.source-view pre {
    margin: 0;
    padding: 20px;
    overflow-x: auto;
}

/* Rendered View (Azure DevOps Style) */
.rendered-view {
    background: white;
    color: #24292f;
    padding: 24px;
    font-family: "Segoe UI", -apple-system, BlinkMacSystemFont, sans-serif;
    font-size: 14px;
    line-height: 1.6;
    overflow-x: auto;
    border-bottom-left-radius: 12px;
    border-bottom-right-radius: 12px;
}

[data-theme="dark"] .rendered-view {
    background: #0d1117;
    color: #c9d1d9;
}

.rendered-view h1, .rendered-view h2, .rendered-view h3 {
    margin-top: 24px;
    margin-bottom: 16px;
    font-weight: 600;
    line-height: 1.25;
}

.rendered-view h1 { font-size: 2em; border-bottom: 1px solid var(--color-border); padding-bottom: 0.3em; }
.rendered-view h2 { font-size: 1.5em; border-bottom: 1px solid var(--color-border); padding-bottom: 0.3em; }
.rendered-view h3 { font-size: 1.25em; }

.rendered-view table {
    border-collapse: collapse;
    width: 100%;
    margin-bottom: 16px;
}

.rendered-view th, .rendered-view td {
    padding: 6px 13px;
    border: 1px solid var(--color-border);
}

.rendered-view th {
    font-weight: 600;
    background-color: var(--color-surface);
}

.rendered-view code {
    padding: 0.2em 0.4em;
    margin: 0;
    font-size: 85%;
    background-color: rgba(175, 184, 193, 0.2);
    border-radius: 6px;
    font-family: ui-monospace, SFMono-Regular, SF Mono, Menlo, Consolas, Liberation Mono, monospace;
}

.rendered-view details {
    margin-bottom: 12px;
    border: 1px solid var(--color-border);
    border-radius: 6px;
    padding: 8px;
}

.rendered-view summary {
    cursor: pointer;
    font-weight: 600;
}

/* Markdown Syntax Highlighting */
.md-comment { color: #6a737d; }
.md-heading { color: #0550ae; font-weight: bold; }
.md-bold { font-weight: bold; color: #24292f; }
.md-code { color: #0a3069; background-color: rgba(175, 184, 193, 0.2); border-radius: 3px; }
.md-link { color: #0969da; }
.md-list { color: #d73a49; }
.md-quote { color: #57606a; border-left: 4px solid #d0d7de; padding-left: 8px; }

[data-theme="dark"] .md-heading { color: #79c0ff; }
[data-theme="dark"] .md-bold { color: #e6edf3; }
[data-theme="dark"] .md-code { color: #a5d6ff; background-color: rgba(110, 118, 129, 0.4); }
[data-theme="dark"] .md-link { color: #58a6ff; }

/* Fullscreen Mode */
.code-block.fullscreen {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: 1000;
    margin: 0;
    border-radius: 0;
    display: flex;
    flex-direction: column;
}

.code-block.fullscreen .example-content {
    flex: 1;
    overflow: auto;
}

.code-block.fullscreen .view-pane {
    height: 100%;
    overflow: auto;
}

.code-block.fullscreen .rendered-view {
    border-radius: 0;
    min-height: 100%;
}
EOF
